import { createContext, useContext, useState } from "react";

const CartContext = createContext();

export function CartProvider({ children }) {
  const [cartItems, setCartItems] = useState([]);
  const [isCartOpen, setIsCartOpen] = useState(false);

  const addToCart = (item) => {
    setCartItems(prevItems => {
      const existingItem = prevItems.find(i => i.id === item.id && i.size === item.size);
      if (existingItem) {
        return prevItems.map(i => 
          i.id === item.id && i.size === item.size 
            ? { ...i, quantity: i.quantity + item.quantity }
            : i
        );
      }
      return [...prevItems, item];
    });
    setIsCartOpen(true);
  };

  const removeItem = (itemId) => {
    setCartItems(prevItems => {
      const itemToRemove = prevItems.find(item => item.cartItemId === itemId || item.id === itemId);
      if (!itemToRemove) return prevItems;
      
      const indexToRemove = prevItems.findIndex(item => 
        item.cartItemId === itemToRemove.cartItemId || 
        (item.id === itemToRemove.id && item.size === itemToRemove.size)
      );
      
      if (indexToRemove === -1) return prevItems;
      
      return [
        ...prevItems.slice(0, indexToRemove),
        ...prevItems.slice(indexToRemove + 1)
      ];
    });
  };

  const updateQuantity = (itemId, newQuantity, newSize = null) => {
    setCartItems(prevItems => 
      prevItems.map(item => {
        if (item.cartItemId === itemId || item.id === itemId) {
          return {
            ...item,
            quantity: newQuantity,
            ...(newSize && { size: newSize })
          };
        }
        return item;
      })
    );
  };

  const toggleCart = () => {
    setIsCartOpen((prev) => !prev);
  };

  return (
    <CartContext.Provider
      value={{
        cartItems,
        setCartItems,
        isCartOpen,
        setIsCartOpen,
        addToCart,
        removeItem,
        updateQuantity,
        toggleCart,
      }}
    >
      {children}
    </CartContext.Provider>
  );
}

export function useCart() {
  return useContext(CartContext);
}"],"names":[],"mappings":";;;;;AACA;AADA;;;AAGA,MAAM,4BAAc,CAAA,GAAA,qMAAA,CAAA,gBAAa,AAAD;AAEzB,SAAS,aAAa,EAAE,QAAQ,EAAE;IACvC,MAAM,CAAC,WAAW,aAAa,GAAG,CAAA,GAAA,qMAAA,CAAA,WAAQ,AAAD,EAAE,EAAE;IAC7C,MAAM,CAAC,YAAY,cAAc,GAAG,CAAA,GAAA,qMAAA,CAAA,WAAQ,AAAD,EAAE;IAE7C,MAAM,YAAY,CAAC;QACjB,aAAa,CAAA;YACX,MAAM,eAAe,UAAU,IAAI,CAAC,CAAA,IAAK,EAAE,EAAE,KAAK,KAAK,EAAE,IAAI,EAAE,IAAI,KAAK,KAAK,IAAI;YACjF,IAAI,cAAc;gBAChB,OAAO,UAAU,GAAG,CAAC,CAAA,IACnB,EAAE,EAAE,KAAK,KAAK,EAAE,IAAI,EAAE,IAAI,KAAK,KAAK,IAAI,GACpC;wBAAE,GAAG,CAAC;wBAAE,UAAU,EAAE,QAAQ,GAAG,KAAK,QAAQ;oBAAC,IAC7C;YAER;YACA,OAAO;mBAAI;gBAAW;aAAK;QAC7B;QACA,cAAc;IAChB;IAEA,MAAM,aAAa,CAAC;QAClB,aAAa,CAAA;YACX,MAAM,eAAe,UAAU,IAAI,CAAC,CAAA,OAAQ,KAAK,UAAU,KAAK,UAAU,KAAK,EAAE,KAAK;YACtF,IAAI,CAAC,cAAc,OAAO;YAE1B,MAAM,gBAAgB,UAAU,SAAS,CAAC,CAAA,OACxC,KAAK,UAAU,KAAK,aAAa,UAAU,IAC1C,KAAK,EAAE,KAAK,aAAa,EAAE,IAAI,KAAK,IAAI,KAAK,aAAa,IAAI;YAGjE,IAAI,kBAAkB,CAAC,GAAG,OAAO;YAEjC,OAAO;mBACF,UAAU,KAAK,CAAC,GAAG;mBACnB,UAAU,KAAK,CAAC,gBAAgB;aACpC;QACH;IACF;IAEA,MAAM,iBAAiB,CAAC,QAAQ,aAAa,UAAU,IAAI;QACzD,aAAa,CAAA,YACX,UAAU,GAAG,CAAC,CAAA;gBACZ,IAAI,KAAK,UAAU,KAAK,UAAU,KAAK,EAAE,KAAK,QAAQ;oBACpD,OAAO;wBACL,GAAG,IAAI;wBACP,UAAU;wBACV,GAAI,WAAW;4BAAE,MAAM;wBAAQ,CAAC;oBAClC;gBACF;gBACA,OAAO;YACT;IAEJ;IAEA,MAAM,aAAa;QACjB,cAAc,CAAC,OAAS,CAAC;IAC3B;IAEA,qBACE,8OAAC,YAAY,QAAQ;QACnB,OAAO;YACL;YACA;YACA;YACA;YACA;YACA;YACA;YACA;QACF;kBAEC;;;;;;AAGP;AAEO,SAAS;IACd,OAAO,CAAA,GAAA,qMAAA,CAAA,aAAU,AAAD,EAAE;AACpB","debugId":null}},
