import { motion } from "framer-motion";
import { useRef, useEffect } from "react";
import { useCart } from '../context/CartContext';
import Image from 'next/image';

export default function CartDropdown({ navigateWithLoader }) {
  const { cartItems, isCartOpen, setIsCartOpen } = useCart();
  const cartRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (event) => {
      const cartIcon = document.querySelector('[data-testid="cart-icon"]');
      if (cartRef.current && !cartRef.current.contains(event.target) && event.target !== cartIcon) {
        setIsCartOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, [setIsCartOpen]);

  // Tính tổng giá trị giỏ hàng
  const total = cartItems.reduce((sum, item) => {
    const itemPrice = parseFloat(item.price) || 0;
    const itemQuantity = parseInt(item.quantity) || 0;
    return sum + (itemPrice * itemQuantity);
  }, 0);

  return (
    <motion.div
      ref={cartRef}
      initial={{ opacity: 0, y: -10 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -10 }}
      transition={{ duration: 0.3, ease: "easeInOut" }}
      className={`absolute top-[55px] right-4 sm:right-10 w-72 sm:w-120 h-80 sm:h-100 bg-white shadow-md flex flex-col z-50 ${!isCartOpen && 'hidden'}`}
    >
      <div className="text-sm font-semibold text-gray-800 border-b border-gray-200 px-4 sm:px-6 py-3 sticky top-0 bg-white z-10 flex justify-between items-center">
        <span>Giỏ hàng của bạn</span>
        <button 
          onClick={() => setIsCartOpen(false)}
          className="text-xs text-gray-500 hover:text-black"
        >
          ✕
        </button>
      </div>
      <div className="flex-1 overflow-y-auto px-4 sm:px-6 py-4">
        {cartItems.length === 0 ? (
          <div className="h-full flex justify-center items-center">
            <p className="text-sm text-gray-500">Giỏ hàng của bạn đang trống!</p>
          </div>
        ) : (
          <div className="space-y-4">
            {cartItems.map((item, index) => {
              const itemPrice = parseFloat(item.price) || 0;
              const itemQuantity = parseInt(item.quantity) || 0;
              const itemTotal = itemPrice * itemQuantity;

              return (
                <div 
                  key={`${item.name}-${item.size}-${item.color}-${index}`} 
                  className="flex items-start gap-3 pb-4 border-b border-gray-100 last:border-0"
                >
                  <div className="relative w-20 h-20 flex-shrink-0 bg-gray-100">
                    {item.image && item.image !== "" ? (
                      <Image
                        src={item.image}
                        alt={item.name || 'Product image'}
                        fill
                        className="object-cover"
                        sizes="80px"
                      />
                    ) : (
                      <div className="w-full h-full flex items-center justify-center bg-gray-100">
                        <span className="text-xs text-gray-400">No image</span>
                      </div>
                    )}
                  </div>
                  <div className="flex-1 min-w-0">
                    <h3 className="text-sm font-semibold text-gray-900 truncate">
                      {item.name || 'Unnamed product'}
                    </h3>
                    <div className="mt-1 text-[10px] text-gray-500 space-y-0.5">
                      {item.size && <p>Size: {item.size}</p>}
                      {item.color && <p>Màu: {item.color}</p>}
                      <p>Số lượng: {itemQuantity}</p>
                    </div>
                    <p className="mt-2 text-sm font-semibold text-gray-900">
                      {itemTotal.toLocaleString('vi-VN')} VND
                    </p>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>
      {cartItems.length > 0 && (
        <div className="border-t border-gray-200 px-4 sm:px-6 py-3 sticky bottom-0 bg-white z-10 space-y-2">
          <div className="flex justify-between items-center mb-3">
            <span className="text-sm font-medium text-gray-900">Tổng cộng:</span>
            <span className="text-sm font-semibold text-gray-900">
              {total.toLocaleString('vi-VN')} VND
            </span>
          </div>
          <button
            className="w-full bg-black text-white text-sm font-semibold py-2 px-4 hover:bg-gray-800 transition-colors"
            onClick={() => navigateWithLoader("/checkout")}
          >
            Thanh toán
          </button>
          <button
            className="w-full bg-white text-black text-sm font-semibold py-2 px-4 border border-black hover:bg-gray-100 transition-colors"
            onClick={() => navigateWithLoader("/cart")}
          >
            Xem giỏ hàng
          </button>
        </div>
      )}
    </motion.div>
  );
}"],"names":[],"mappings":";;;;AACA;AACA;AACA;AACA;AAJA;;;;;;AAMe,SAAS,aAAa,EAAE,kBAAkB,EAAE;IACzD,MAAM,EAAE,SAAS,EAAE,UAAU,EAAE,aAAa,EAAE,GAAG,CAAA,GAAA,6HAAA,CAAA,UAAO,AAAD;IACvD,MAAM,UAAU,CAAA,GAAA,qMAAA,CAAA,SAAM,AAAD,EAAE;IAEvB,CAAA,GAAA,qMAAA,CAAA,YAAS,AAAD,EAAE;QACR,MAAM,qBAAqB,CAAC;YAC1B,MAAM,WAAW,SAAS,aAAa,CAAC;YACxC,IAAI,QAAQ,OAAO,IAAI,CAAC,QAAQ,OAAO,CAAC,QAAQ,CAAC,MAAM,MAAM,KAAK,MAAM,MAAM,KAAK,UAAU;gBAC3F,cAAc;YAChB;QACF;QACA,SAAS,gBAAgB,CAAC,aAAa;QACvC,OAAO,IAAM,SAAS,mBAAmB,CAAC,aAAa;IACzD,GAAG;QAAC;KAAc;IAElB,6BAA6B;IAC7B,MAAM,QAAQ,UAAU,MAAM,CAAC,CAAC,KAAK;QACnC,MAAM,YAAY,WAAW,KAAK,KAAK,KAAK;QAC5C,MAAM,eAAe,SAAS,KAAK,QAAQ,KAAK;QAChD,OAAO,MAAO,YAAY;IAC5B,GAAG;IAEH,qBACE,8OAAC,0LAAA,CAAA,SAAM,CAAC,GAAG;QACT,KAAK;QACL,SAAS;YAAE,SAAS;YAAG,GAAG,CAAC;QAAG;QAC9B,SAAS;YAAE,SAAS;YAAG,GAAG;QAAE;QAC5B,MAAM;YAAE,SAAS;YAAG,GAAG,CAAC;QAAG;QAC3B,YAAY;YAAE,UAAU;YAAK,MAAM;QAAY;QAC/C,WAAW,CAAC,0GAA0G,EAAE,CAAC,cAAc,UAAU;;0BAEjJ,8OAAC;gBAAI,WAAU;;kCACb,8OAAC;kCAAK;;;;;;kCACN,8OAAC;wBACC,SAAS,IAAM,cAAc;wBAC7B,WAAU;kCACX;;;;;;;;;;;;0BAIH,8OAAC;gBAAI,WAAU;0BACZ,UAAU,MAAM,KAAK,kBACpB,8OAAC;oBAAI,WAAU;8BACb,cAAA,8OAAC;wBAAE,WAAU;kCAAwB;;;;;;;;;;yCAGvC,8OAAC;oBAAI,WAAU;8BACZ,UAAU,GAAG,CAAC,CAAC,MAAM;wBACpB,MAAM,YAAY,WAAW,KAAK,KAAK,KAAK;wBAC5C,MAAM,eAAe,SAAS,KAAK,QAAQ,KAAK;wBAChD,MAAM,YAAY,YAAY;wBAE9B,qBACE,8OAAC;4BAEC,WAAU;;8CAEV,8OAAC;oCAAI,WAAU;8CACZ,KAAK,KAAK,IAAI,KAAK,KAAK,KAAK,mBAC5B,8OAAC,6HAAA,CAAA,UAAK;wCACJ,KAAK,KAAK,KAAK;wCACf,KAAK,KAAK,IAAI,IAAI;wCAClB,IAAI;wCACJ,WAAU;wCACV,OAAM;;;;;6DAGR,8OAAC;wCAAI,WAAU;kDACb,cAAA,8OAAC;4CAAK,WAAU;sDAAwB;;;;;;;;;;;;;;;;8CAI9C,8OAAC;oCAAI,WAAU;;sDACb,8OAAC;4CAAG,WAAU;sDACX,KAAK,IAAI,IAAI;;;;;;sDAEhB,8OAAC;4CAAI,WAAU;;gDACZ,KAAK,IAAI,kBAAI,8OAAC;;wDAAE;wDAAO,KAAK,IAAI;;;;;;;gDAChC,KAAK,KAAK,kBAAI,8OAAC;;wDAAE;wDAAM,KAAK,KAAK;;;;;;;8DAClC,8OAAC;;wDAAE;wDAAW;;;;;;;;;;;;;sDAEhB,8OAAC;4CAAE,WAAU;;gDACV,UAAU,cAAc,CAAC;gDAAS;;;;;;;;;;;;;;2BA5BlC,GAAG,KAAK,IAAI,CAAC,CAAC,EAAE,KAAK,IAAI,CAAC,CAAC,EAAE,KAAK,KAAK,CAAC,CAAC,EAAE,OAAO;;;;;oBAiC7D;;;;;;;;;;;YAIL,UAAU,MAAM,GAAG,mBAClB,8OAAC;gBAAI,WAAU;;kCACb,8OAAC;wBAAI,WAAU;;0CACb,8OAAC;gCAAK,WAAU;0CAAoC;;;;;;0CACpD,8OAAC;gCAAK,WAAU;;oCACb,MAAM,cAAc,CAAC;oCAAS;;;;;;;;;;;;;kCAGnC,8OAAC;wBACC,WAAU;wBACV,SAAS,IAAM,mBAAmB;kCACnC;;;;;;kCAGD,8OAAC;wBACC,WAAU;wBACV,SAAS,IAAM,mBAAmB;kCACnC;;;;;;;;;;;;;;;;;;AAOX","debugId":null}},
    {"offset": {"line": 569, "column": 0}, "map": {"version":3,"sources":["file://C%3A/Users/hongh/aishh/src/components/MenuDropdown.js"],"sourcesContent":[""use client";
import { useState, useEffect, useMemo, useRef } from "react";

export default function MenuDropdown({
  isOpen,
  setIsOpen,
  menuItems,
  activeSubMenu,
  setActiveSubMenu,
  handleNavigation,
}) {
  const [isMobile, setIsMobile] = useState(false);
  const [activeMobileSubMenu, setActiveMobileSubMenu] = useState(null);
  const menuRef = useRef(null);

  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth < 768);
    };
    checkMobile();
    window.addEventListener("resize", checkMobile);
    return () => window.removeEventListener("resize", checkMobile);
  }, []);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        isOpen &&
        menuRef.current &&
        !menuRef.current.contains(event.target) &&
        !event.target.closest(".menu-item")
      ) {
        console.log("Clicked outside, closing menu", event.target);
        setIsOpen(false);
        setActiveSubMenu(null);
        setActiveMobileSubMenu(null);
      }
    };
    document.addEventListener("click", handleClickOutside);
    return () => document.removeEventListener("click", handleClickOutside);
  }, [isOpen, setIsOpen]);

  // Desktop Menu
  const DesktopMenu = () => (
    <div
      ref={menuRef}
      className="absolute top-full left-0 w-1/2 h-screen bg-white shadow-lg p-4 overflow-y-auto flex"
      onClick={(e) => e.stopPropagation()}
    >
      <div className="w-1/2 flex flex-col gap-2 text-xs font-semibold text-black">
        {menuItems.map((item, index) => (
          <div key={index} className="p-2">
            <span className="text-sm text-gray-800 relative inline-block group">
              {item.title}
              <span className="absolute bottom-0 left-0 w-0 h-[1px] bg-black transition-all duration-300 group-hover:w-full"></span>
            </span>
            <div className="flex flex-col mt-2 text-xs font-normal">
              {item.subItems.map((sub, subIndex) => (
                <div
                  key={subIndex}
                  className="relative p-2 cursor-pointer group menu-item"
                  onClick={(e) => {
                    e.stopPropagation();
                    console.log(`Clicked: ${item.title} -> ${sub.title}`);
                    sub.subItems.length > 0
                      ? setActiveSubMenu(activeSubMenu === sub.title ? null : sub.title)
                      : handleNavigation(item.title, sub.title);
                  }}
                >
                  <span className="relative inline-block">
                    {sub.title}
                    <span className="absolute bottom-0 left-0 w-0 h-[1px] bg-gray-500 transition-all duration-300 group-hover:w-full"></span>
                  </span>
                  {sub.subItems.length > 0 && (
                    <span className="text-[8px] absolute right-2">▶</span>
                  )}
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
      {activeSubMenu && (
        <div className="w-1/2 h-full bg-white p-4 flex flex-col justify-start">
          {menuItems.flatMap((item) => item.subItems).map((sub, subIndex) =>
            activeSubMenu === sub.title && sub.subItems.length > 0 ? (
              <div
                key={subIndex}
