        },
        { 
          label: "Account Settings",
          path: "/settings" 
        },
        { 
          label: "Đăng xuất", 
          path: "/logout" 
        },
      ]
    : [
        { 
          label: "Đăng nhập", 
          path: "/login" 
        },
        { 
          label: "Đơn hàng của tôi", 
