import { useState, useEffect } from "react";
import { motion } from "framer-motion";

export default function AnnouncementBanner() {
  const [announcement, setAnnouncement] = useState("");
  const [isActive, setIsActive] = useState(false);

  useEffect(() => {
    fetchAnnouncement();
    const interval = setInterval(fetchAnnouncement, 30000); // Cập nhật mỗi 30 giây
    return () => clearInterval(interval);
  }, []);

  const fetchAnnouncement = async () => {
    try {
      const response = await fetch("/api/admin/notifications");
      const data = await response.json();
      if (data.ok) {
        setAnnouncement(data.announcement);
        setIsActive(data.isAnnouncementActive);
      }
    } catch (error) {
      console.error("Error fetching announcement:", error);
    }
  };

  if (!isActive || !announcement) return null;

  return (
    <div className="bg-black text-white overflow-hidden">
      <motion.div
        initial={{ x: "100%" }}
        animate={{ x: "-100%" }}
        transition={{
          x: {
            repeat: Infinity,
            repeatType: "loop",
            duration: 20,
            ease: "linear",
          },
        }}
        className="whitespace-nowrap py-2 text-sm"
      >
        {announcement}
      </motion.div>
    </div>
  );
} "],"names":[],"mappings":";;;;AACA;AACA;AAFA;;;;AAIe,SAAS;IACtB,MAAM,CAAC,cAAc,gBAAgB,GAAG,CAAA,GAAA,qMAAA,CAAA,WAAQ,AAAD,EAAE;IACjD,MAAM,CAAC,UAAU,YAAY,GAAG,CAAA,GAAA,qMAAA,CAAA,WAAQ,AAAD,EAAE;IAEzC,CAAA,GAAA,qMAAA,CAAA,YAAS,AAAD,EAAE;QACR;QACA,MAAM,WAAW,YAAY,mBAAmB,QAAQ,uBAAuB;QAC/E,OAAO,IAAM,cAAc;IAC7B,GAAG,EAAE;IAEL,MAAM,oBAAoB;QACxB,IAAI;YACF,MAAM,WAAW,MAAM,MAAM;YAC7B,MAAM,OAAO,MAAM,SAAS,IAAI;YAChC,IAAI,KAAK,EAAE,EAAE;gBACX,gBAAgB,KAAK,YAAY;gBACjC,YAAY,KAAK,oBAAoB;YACvC;QACF,EAAE,OAAO,OAAO;YACd,QAAQ,KAAK,CAAC,gCAAgC;QAChD;IACF;IAEA,IAAI,CAAC,YAAY,CAAC,cAAc,OAAO;IAEvC,qBACE,8OAAC;QAAI,WAAU;kBACb,cAAA,8OAAC,0LAAA,CAAA,SAAM,CAAC,GAAG;YACT,SAAS;gBAAE,GAAG;YAAO;YACrB,SAAS;gBAAE,GAAG;YAAQ;YACtB,YAAY;gBACV,GAAG;oBACD,QAAQ;oBACR,YAAY;oBACZ,UAAU;oBACV,MAAM;gBACR;YACF;YACA,WAAU;sBAET;;;;;;;;;;;AAIT","debugId":null}}]
