    {"offset": {"line": 7, "column": 0}, "map": {"version":3,"sources":["file://C%3A/Users/hongh/aishh/src/hooks/useProductFilters.ts"],"sourcesContent":["import { useState, useMemo } from "react";
import { Product } from "../types/product";

export const useProductFilters = (initialProducts: Product[]) => {
  const [selectedColors, setSelectedColors] = useState<string[]>([]);
  const [selectedSizes, setSelectedSizes] = useState<string[]>([]);
  const [sortOption, setSortOption] = useState<string>("newest");

  const sortedProducts = useMemo(() => {
    let filteredProducts = [...initialProducts];

    // Lọc theo màu sắc
    if (selectedColors.length > 0) {
      filteredProducts = filteredProducts.filter(product => {
        if (!product.colors || !Array.isArray(product.colors)) return false;
        return selectedColors.some(selectedColor => 
          product.colors.some(color => color.toLowerCase() === selectedColor.toLowerCase())
        );
      });
    }

    // Lọc theo kích thước
    if (selectedSizes.length > 0) {
      filteredProducts = filteredProducts.filter(product => {
        const availableSizes: string[] = [];
        if (product.quantityM > 0) availableSizes.push('m');
        if (product.quantityL > 0) availableSizes.push('l');
        if (product.quantityXL > 0) availableSizes.push('xl');
        return selectedSizes.some(size => availableSizes.includes(size.toLowerCase()));
      });
    }

    // Sắp xếp sản phẩm
    switch (sortOption) {
      case "price-high-to-low":
        filteredProducts.sort((a, b) => b.price - a.price);
        break;
      case "price-low-to-high":
        filteredProducts.sort((a, b) => a.price - b.price);
        break;
      case "newest":
      default:
        filteredProducts.sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime());
        break;
    }

    return filteredProducts;
  }, [initialProducts, selectedColors, selectedSizes, sortOption]);

  return {
    selectedColors,
    setSelectedColors,
    selectedSizes,
    setSelectedSizes,
    sortOption,
    setSortOption,
    sortedProducts,
  };
}; "],"names":[],"mappings":";;;AAAA;;AAGO,MAAM,oBAAoB,CAAC;IAChC,MAAM,CAAC,gBAAgB,kBAAkB,GAAG,CAAA,GAAA,qMAAA,CAAA,WAAQ,AAAD,EAAY,EAAE;IACjE,MAAM,CAAC,eAAe,iBAAiB,GAAG,CAAA,GAAA,qMAAA,CAAA,WAAQ,AAAD,EAAY,EAAE;IAC/D,MAAM,CAAC,YAAY,cAAc,GAAG,CAAA,GAAA,qMAAA,CAAA,WAAQ,AAAD,EAAU;IAErD,MAAM,iBAAiB,CAAA,GAAA,qMAAA,CAAA,UAAO,AAAD,EAAE;QAC7B,IAAI,mBAAmB;eAAI;SAAgB;QAE3C,mBAAmB;QACnB,IAAI,eAAe,MAAM,GAAG,GAAG;YAC7B,mBAAmB,iBAAiB,MAAM,CAAC,CAAA;gBACzC,IAAI,CAAC,QAAQ,MAAM,IAAI,CAAC,MAAM,OAAO,CAAC,QAAQ,MAAM,GAAG,OAAO;gBAC9D,OAAO,eAAe,IAAI,CAAC,CAAA,gBACzB,QAAQ,MAAM,CAAC,IAAI,CAAC,CAAA,QAAS,MAAM,WAAW,OAAO,cAAc,WAAW;YAElF;QACF;QAEA,sBAAsB;QACtB,IAAI,cAAc,MAAM,GAAG,GAAG;YAC5B,mBAAmB,iBAAiB,MAAM,CAAC,CAAA;gBACzC,MAAM,iBAA2B,EAAE;gBACnC,IAAI,QAAQ,SAAS,GAAG,GAAG,eAAe,IAAI,CAAC;gBAC/C,IAAI,QAAQ,SAAS,GAAG,GAAG,eAAe,IAAI,CAAC;gBAC/C,IAAI,QAAQ,UAAU,GAAG,GAAG,eAAe,IAAI,CAAC;gBAChD,OAAO,cAAc,IAAI,CAAC,CAAA,OAAQ,eAAe,QAAQ,CAAC,KAAK,WAAW;YAC5E;QACF;QAEA,mBAAmB;QACnB,OAAQ;YACN,KAAK;gBACH,iBAAiB,IAAI,CAAC,CAAC,GAAG,IAAM,EAAE,KAAK,GAAG,EAAE,KAAK;gBACjD;YACF,KAAK;gBACH,iBAAiB,IAAI,CAAC,CAAC,GAAG,IAAM,EAAE,KAAK,GAAG,EAAE,KAAK;gBACjD;YACF,KAAK;YACL;gBACE,iBAAiB,IAAI,CAAC,CAAC,GAAG,IAAM,IAAI,KAAK,EAAE,SAAS,EAAE,OAAO,KAAK,IAAI,KAAK,EAAE,SAAS,EAAE,OAAO;gBAC/F;QACJ;QAEA,OAAO;IACT,GAAG;QAAC;QAAiB;QAAgB;QAAe;KAAW;IAE/D,OAAO;QACL;QACA;QACA;QACA;QACA;QACA;QACA;IACF;AACF","debugId":null}},
