import { motion, AnimatePresence } from "framer-motion";
import Link from "next/link";
import { useWishlist } from '../context/WishlistContext';
import { Product } from '../types/product';
import Image from "next/image";
import { useRef, useState } from "react";

interface ProductCardProps {
  product: Product;
  currentImageIndex?: number;
  // handlePrevImage: (e: React.MouseEvent, productId: string) => void;
  // handleNextImage: (e: React.MouseEvent, productId: string) => void;
  // onHover: (productId: string) => void;
  // onLeave: () => void;
}

export default function ProductCard({
  product,
}: ProductCardProps) {
  const { toggleWishlist, isInWishlist } = useWishlist();
  const isLiked = isInWishlist(product._id);
  const [imageIndex, setImageIndex] = useState(0);
  const intervalRef = useRef<NodeJS.Timeout | null>(null);

  // Ensure we have valid images
  const defaultImage = "/images/placeholder.jpg"; // Add a placeholder image to your public folder
  const images = product.images && product.images.length > 0 ? product.images : [defaultImage];
  const currentImageIndex = imageIndex < images.length ? imageIndex : 0;

  const handleMouseEnter = () => {
    if (images.length <= 1) return;
    intervalRef.current = setInterval(() => {
      setImageIndex(prev => (prev + 1) % images.length);
    }, 1000);
  };

  const handleMouseLeave = () => {
    if (intervalRef.current) clearInterval(intervalRef.current);
    setImageIndex(0);
  };

  return (
    <Link href={`/products/${product._id}`} className="block">
      <motion.div
        className="relative group"
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.4, delay: Number(product._id) * 0.1 }}
      >
        <div className="w-full overflow-hidden rounded-md">
          <div className="relative h-[250px] sm:h-[350px]">
            <Image
              src={images[currentImageIndex]}
              alt={product.name || "Product Image"}
              fill
              quality={100}
              className="object-contain object-center"
              sizes="(max-width: 768px) 50vw, 25vw"
              priority={currentImageIndex === 0}
            />
          </div>
        </div>
        <div className="p-4 flex flex-col items-center text-center">
          <h2 className="text-[11px] sm:text-[12px] md:text-[13px] font-bold text-black uppercase tracking-wide">
            {product.name}
          </h2>
          <p className="text-[11px] sm:text-[12px] md:text-sm text-gray-600 mt-3 sm:mt-4 font-medium">
            {product.price.toLocaleString('vi-VN')} VND
          </p>
          <span className="relative text-[9px] sm:text-[10px] md:text-[11px] font-bold uppercase text-black tracking-widest mt-1 sm:mt-2 inline-block group">
            Shop This
            <span className="absolute bottom-0 left-0 w-0 h-[1px] bg-black transition-all duration-300 group-hover:w-full" />
          </span>
        </div>
        <div className="absolute top-1 right-1 sm:top-2 sm:right-2">
          <button
            onClick={(e) => {
              e.preventDefault();
              e.stopPropagation();
              toggleWishlist(product._id);
            }}
            className="text-black hover:text-gray-500 transition duration-300 bg-white bg-opacity-70 rounded-full p-0.5 sm:p-1"
          >
            <AnimatePresence>
              <motion.svg
                key={isLiked ? 'filled' : 'empty'}
                initial={{ scale: 0.8 }}
                animate={{ scale: 1 }}
                exit={{ scale: 0.8 }}
                transition={{ duration: 0.2 }}
                xmlns="http://www.w3.org/2000/svg"
                className="h-3 w-3 sm:h-4 sm:w-4"
                viewBox="0 0 24 24"
                fill={isLiked ? "currentColor" : "none"}
                stroke="currentColor"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={1.5}
                  d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z"
                />
              </motion.svg>
            </AnimatePresence>
          </button>
        </div>
      </motion.div>
    </Link>
  );
} "],"names":[],"mappings":";;;;AAEA;AAAA;AACA;AACA;AAEA;AACA;AAPA;;;;;;;AAkBe,SAAS,YAAY,EAClC,OAAO,EACU;IACjB,MAAM,EAAE,cAAc,EAAE,YAAY,EAAE,GAAG,CAAA,GAAA,kIAAA,CAAA,cAAW,AAAD;IACnD,MAAM,UAAU,aAAa,QAAQ,GAAG;IACxC,MAAM,CAAC,YAAY,cAAc,GAAG,CAAA,GAAA,qMAAA,CAAA,WAAQ,AAAD,EAAE;IAC7C,MAAM,cAAc,CAAA,GAAA,qMAAA,CAAA,SAAM,AAAD,EAAyB;IAElD,8BAA8B;IAC9B,MAAM,eAAe,2BAA2B,gDAAgD;IAChG,MAAM,SAAS,QAAQ,MAAM,IAAI,QAAQ,MAAM,CAAC,MAAM,GAAG,IAAI,QAAQ,MAAM,GAAG;QAAC;KAAa;IAC5F,MAAM,oBAAoB,aAAa,OAAO,MAAM,GAAG,aAAa;IAEpE,MAAM,mBAAmB;QACvB,IAAI,OAAO,MAAM,IAAI,GAAG;QACxB,YAAY,OAAO,GAAG,YAAY;YAChC,cAAc,CAAA,OAAQ,CAAC,OAAO,CAAC,IAAI,OAAO,MAAM;QAClD,GAAG;IACL;IAEA,MAAM,mBAAmB;QACvB,IAAI,YAAY,OAAO,EAAE,cAAc,YAAY,OAAO;QAC1D,cAAc;IAChB;IAEA,qBACE,8OAAC,4JAAA,CAAA,UAAI;QAAC,MAAM,CAAC,UAAU,EAAE,QAAQ,GAAG,EAAE;QAAE,WAAU;kBAChD,cAAA,8OAAC,0LAAA,CAAA,SAAM,CAAC,GAAG;YACT,WAAU;YACV,cAAc;YACd,cAAc;YACd,SAAS;gBAAE,SAAS;gBAAG,GAAG;YAAG;YAC7B,SAAS;gBAAE,SAAS;gBAAG,GAAG;YAAE;YAC5B,YAAY;gBAAE,UAAU;gBAAK,OAAO,OAAO,QAAQ,GAAG,IAAI;YAAI;;8BAE9D,8OAAC;oBAAI,WAAU;8BACb,cAAA,8OAAC;wBAAI,WAAU;kCACb,cAAA,8OAAC,6HAAA,CAAA,UAAK;4BACJ,KAAK,MAAM,CAAC,kBAAkB;4BAC9B,KAAK,QAAQ,IAAI,IAAI;4BACrB,IAAI;4BACJ,SAAS;4BACT,WAAU;4BACV,OAAM;4BACN,UAAU,sBAAsB;;;;;;;;;;;;;;;;8BAItC,8OAAC;oBAAI,WAAU;;sCACb,8OAAC;4BAAG,WAAU;sCACX,QAAQ,IAAI;;;;;;sCAEf,8OAAC;4BAAE,WAAU;;gCACV,QAAQ,KAAK,CAAC,cAAc,CAAC;gCAAS;;;;;;;sCAEzC,8OAAC;4BAAK,WAAU;;gCAAmI;8CAEjJ,8OAAC;oCAAK,WAAU;;;;;;;;;;;;;;;;;;8BAGpB,8OAAC;oBAAI,WAAU;8BACb,cAAA,8OAAC;wBACC,SAAS,CAAC;4BACR,EAAE,cAAc;4BAChB,EAAE,eAAe;4BACjB,eAAe,QAAQ,GAAG;wBAC5B;wBACA,WAAU;kCAEV,cAAA,8OAAC,yLAAA,CAAA,kBAAe;sCACd,cAAA,8OAAC,0LAAA,CAAA,SAAM,CAAC,GAAG;gCAET,SAAS;oCAAE,OAAO;gCAAI;gCACtB,SAAS;oCAAE,OAAO;gCAAE;gCACpB,MAAM;oCAAE,OAAO;gCAAI;gCACnB,YAAY;oCAAE,UAAU;gCAAI;gCAC5B,OAAM;gCACN,WAAU;gCACV,SAAQ;gCACR,MAAM,UAAU,iBAAiB;gCACjC,QAAO;0CAEP,cAAA,8OAAC;oCACC,eAAc;oCACd,gBAAe;oCACf,aAAa;oCACb,GAAE;;;;;;+BAfC,UAAU,WAAW;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;;AAwB1C","debugId":null}},
