    if (!product || !selectedSize) return;

    const sizeQuantity = product[`quantity${selectedSize}` as keyof Product] as number;
    if (value > sizeQuantity) {
      toast.error(`Chỉ còn ${sizeQuantity} sản phẩm size ${selectedSize}`);
      return;
    }

    setQuantity(value);
  };

