      <div className="flex justify-between items-center px-8 py-4 text-xs text-gray-600 border-t">
        <div className="text-black">
          <Link href="/" className="hover:underline font-semibold uppercase">Home</Link>
          {" / "}
          <Link href="/products" className="hover:underline font-semibold uppercase">Products</Link>
          {" / "}
          <Link href={`/products/${product.category.toLowerCase()}`} className="hover:underline font-semibold uppercase">{product.category}</Link>
          {" / "}
          <span className="font-semibold uppercase">{product.name}</span>
        </div>
        <div>
          <button 
            onClick={() => setShowHelpPanel(true)}
            className="hover:underline text-black font-semibold uppercase"
          >
            Cần giúp đỡ?
          </button>
        </div>
      </div>

      {/* Full Image Modal */}
      {showFullImage && (
        <div 
          className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50" 
          onClick={() => setShowFullImage(false)}
        >
          <div className="relative w-full h-full overflow-auto flex justify-center items-center">
            <Image
              src={product.images && product.images[currentImageIndex] ? product.images[currentImageIndex] : "/placeholder.jpg"}
              alt={product.name}
              width={1000}
              height={1000}
              quality={100}
              className="object-contain w-full h-full"
              style={{ maxWidth: "100%", height: "auto" }}
            />
          </div>
        </div>
      )}

      {/* Help Panel */}
      <HelpPanel isOpen={showHelpPanel} onClose={() => setShowHelpPanel(false)} />

      {/* Size Guide Modal */}
      {showSizeGuide && (
        <div 
          className="fixed inset-0 bg-white/60 backdrop-blur-sm flex items-center justify-center z-50" 
          onClick={() => setShowSizeGuide(false)}
        >
          <div 
            className="bg-white p-6 rounded-lg max-w-2xl w-full mx-4 shadow-lg"
            onClick={e => e.stopPropagation()}
          >
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-xl font-bold text-left">Hướng dẫn chọn size</h2>
              <button 
                onClick={() => setShowSizeGuide(false)}
                className="text-gray-500 hover:text-black"
              >
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>
            <div>
              <Image
                src={product.sizeGuideImage || "/size-guide.jpg"}
                alt="Size Guide"
                width={600}
                height={400}
                className="w-full h-auto rounded-lg"
              />
            </div>
          </div>
        </div>
      )}
    </div>
