import { Product } from "../types/product";

export const useProductFilters = (initialProducts: Product[]) => {
  const [selectedColors, setSelectedColors] = useState<string[]>([]);
  const [selectedSizes, setSelectedSizes] = useState<string[]>([]);
  const [sortOption, setSortOption] = useState<string>("newest");

  const sortedProducts = useMemo(() => {
    let filteredProducts = [...initialProducts];

    // Lọc theo màu sắc
    if (selectedColors.length > 0) {
      filteredProducts = filteredProducts.filter(product =>
        product.colors && Array.isArray(product.colors) && 
        product.colors.some(color => selectedColors.includes(color))
      );
    }

    // Lọc theo kích thước
    if (selectedSizes.length > 0) {
      filteredProducts = filteredProducts.filter(product =>
        product.sizes && Array.isArray(product.sizes) && 
        product.sizes.some(size => selectedSizes.includes(size))
      );
    }

    // Sắp xếp sản phẩm
    switch (sortOption) {
      case "price-high-to-low":
        filteredProducts.sort((a, b) => b.price - a.price);
        break;
      case "price-low-to-high":
        filteredProducts.sort((a, b) => a.price - b.price);
        break;
      case "newest":
      default:
        filteredProducts.sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime());
        break;
    }

    return filteredProducts;
  }, [initialProducts, selectedColors, selectedSizes, sortOption]);

  return {
    selectedColors,
    setSelectedColors,
    selectedSizes,
    setSelectedSizes,
    sortOption,
    setSortOption,
    sortedProducts,
  };
}; "],"names":[],"mappings":";;;AAAA;;AAGO,MAAM,oBAAoB,CAAC;IAChC,MAAM,CAAC,gBAAgB,kBAAkB,GAAG,CAAA,GAAA,qMAAA,CAAA,WAAQ,AAAD,EAAY,EAAE;IACjE,MAAM,CAAC,eAAe,iBAAiB,GAAG,CAAA,GAAA,qMAAA,CAAA,WAAQ,AAAD,EAAY,EAAE;IAC/D,MAAM,CAAC,YAAY,cAAc,GAAG,CAAA,GAAA,qMAAA,CAAA,WAAQ,AAAD,EAAU;IAErD,MAAM,iBAAiB,CAAA,GAAA,qMAAA,CAAA,UAAO,AAAD,EAAE;QAC7B,IAAI,mBAAmB;eAAI;SAAgB;QAE3C,mBAAmB;QACnB,IAAI,eAAe,MAAM,GAAG,GAAG;YAC7B,mBAAmB,iBAAiB,MAAM,CAAC,CAAA,UACzC,QAAQ,MAAM,IAAI,MAAM,OAAO,CAAC,QAAQ,MAAM,KAC9C,QAAQ,MAAM,CAAC,IAAI,CAAC,CAAA,QAAS,eAAe,QAAQ,CAAC;QAEzD;QAEA,sBAAsB;QACtB,IAAI,cAAc,MAAM,GAAG,GAAG;YAC5B,mBAAmB,iBAAiB,MAAM,CAAC,CAAA,UACzC,QAAQ,KAAK,IAAI,MAAM,OAAO,CAAC,QAAQ,KAAK,KAC5C,QAAQ,KAAK,CAAC,IAAI,CAAC,CAAA,OAAQ,cAAc,QAAQ,CAAC;QAEtD;QAEA,mBAAmB;QACnB,OAAQ;YACN,KAAK;gBACH,iBAAiB,IAAI,CAAC,CAAC,GAAG,IAAM,EAAE,KAAK,GAAG,EAAE,KAAK;gBACjD;YACF,KAAK;gBACH,iBAAiB,IAAI,CAAC,CAAC,GAAG,IAAM,EAAE,KAAK,GAAG,EAAE,KAAK;gBACjD;YACF,KAAK;YACL;gBACE,iBAAiB,IAAI,CAAC,CAAC,GAAG,IAAM,IAAI,KAAK,EAAE,SAAS,EAAE,OAAO,KAAK,IAAI,KAAK,EAAE,SAAS,EAAE,OAAO;gBAC/F;QACJ;QAEA,OAAO;IACT,GAAG;QAAC;QAAiB;QAAgB;QAAe;KAAW;IAE/D,OAAO;QACL;QACA;QACA;QACA;QACA;QACA;QACA;IACF;AACF","debugId":null}},
