    {"offset": {"line": 7, "column": 0}, "map": {"version":3,"sources":["file://C%3A/Users/hongh/aishh/src/hooks/useProductFilters.ts"],"sourcesContent":["import { useState, useMemo } from "react";
import { Product } from "../types/product";

export const useProductFilters = (initialProducts: Product[]) => {
  const [selectedColors, setSelectedColors] = useState<string[]>([]);
  const [selectedSizes, setSelectedSizes] = useState<string[]>([]);
  const [sortOption, setSortOption] = useState<string>("newest");

  const sortedProducts = useMemo(() => {
    let filteredProducts = [...initialProducts];

    // Lọc theo màu sắc
    if (selectedColors.length > 0) {
      filteredProducts = filteredProducts.filter(product =>
        product.colors && Array.isArray(product.colors) && 
        product.colors.some(color => selectedColors.includes(color))
      );
    }

    // Lọc theo kích thước
    if (selectedSizes.length > 0) {
      filteredProducts = filteredProducts.filter(product =>
        product.sizes && Array.isArray(product.sizes) && 
        product.sizes.some(size => selectedSizes.includes(size))
      );
    }

    // Sắp xếp sản phẩm
    switch (sortOption) {
      case "price-high-to-low":
        filteredProducts.sort((a, b) => b.price - a.price);
        break;
      case "price-low-to-high":
        filteredProducts.sort((a, b) => a.price - b.price);
        break;
      case "newest":
      default:
        filteredProducts.sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime());
        break;
    }

    return filteredProducts;
  }, [initialProducts, selectedColors, selectedSizes, sortOption]);

  return {
    selectedColors,
    setSelectedColors,
    selectedSizes,
    setSelectedSizes,
    sortOption,
    setSortOption,
    sortedProducts,
  };
}; "],"names":[],"mappings":";;;AAAA;;;AAGO,MAAM,oBAAoB,CAAC;;IAChC,MAAM,CAAC,gBAAgB,kBAAkB,GAAG,CAAA,GAAA,6JAAA,CAAA,WAAQ,AAAD,EAAY,EAAE;IACjE,MAAM,CAAC,eAAe,iBAAiB,GAAG,CAAA,GAAA,6JAAA,CAAA,WAAQ,AAAD,EAAY,EAAE;IAC/D,MAAM,CAAC,YAAY,cAAc,GAAG,CAAA,GAAA,6JAAA,CAAA,WAAQ,AAAD,EAAU;IAErD,MAAM,iBAAiB,CAAA,GAAA,6JAAA,CAAA,UAAO,AAAD;qDAAE;YAC7B,IAAI,mBAAmB;mBAAI;aAAgB;YAE3C,mBAAmB;YACnB,IAAI,eAAe,MAAM,GAAG,GAAG;gBAC7B,mBAAmB,iBAAiB,MAAM;iEAAC,CAAA,UACzC,QAAQ,MAAM,IAAI,MAAM,OAAO,CAAC,QAAQ,MAAM,KAC9C,QAAQ,MAAM,CAAC,IAAI;yEAAC,CAAA,QAAS,eAAe,QAAQ,CAAC;;;YAEzD;YAEA,sBAAsB;YACtB,IAAI,cAAc,MAAM,GAAG,GAAG;gBAC5B,mBAAmB,iBAAiB,MAAM;iEAAC,CAAA,UACzC,QAAQ,KAAK,IAAI,MAAM,OAAO,CAAC,QAAQ,KAAK,KAC5C,QAAQ,KAAK,CAAC,IAAI;yEAAC,CAAA,OAAQ,cAAc,QAAQ,CAAC;;;YAEtD;YAEA,mBAAmB;YACnB,OAAQ;gBACN,KAAK;oBACH,iBAAiB,IAAI;qEAAC,CAAC,GAAG,IAAM,EAAE,KAAK,GAAG,EAAE,KAAK;;oBACjD;gBACF,KAAK;oBACH,iBAAiB,IAAI;qEAAC,CAAC,GAAG,IAAM,EAAE,KAAK,GAAG,EAAE,KAAK;;oBACjD;gBACF,KAAK;gBACL;oBACE,iBAAiB,IAAI;qEAAC,CAAC,GAAG,IAAM,IAAI,KAAK,EAAE,SAAS,EAAE,OAAO,KAAK,IAAI,KAAK,EAAE,SAAS,EAAE,OAAO;;oBAC/F;YACJ;YAEA,OAAO;QACT;oDAAG;QAAC;QAAiB;QAAgB;QAAe;KAAW;IAE/D,OAAO;QACL;QACA;QACA;QACA;QACA;QACA;QACA;IACF;AACF;GAlDa","debugId":null}},
