import { RefObject } from "react";

interface SortOption {
  value: string;
  label: string;
}

interface SortSectionProps {
  isSortOpen: boolean;
  sortRef: RefObject<HTMLDivElement | null>;
  selectedSort: string;
  handleSortChange: (value: string) => void;
  handleCloseSort: () => void;
  handleApplySort: () => void;
  sortOptions: SortOption[];
  hoveredButton?: string | null;
  activeButton?: string;
  onMouseEnter?: () => void;
  onMouseLeave?: () => void;
}

export default function SortSection({
  isSortOpen,
  sortRef,
  selectedSort,
  handleSortChange,
  handleCloseSort,
  handleApplySort,
  sortOptions = [],
  hoveredButton,
  activeButton,
  onMouseEnter,
  onMouseLeave,
}: SortSectionProps) {
  const getSortOptionText = () => {
    if (!sortOptions || sortOptions.length === 0) return "";
    const found = sortOptions.find((opt) => opt.value === selectedSort);
    return found ? found.label : "";
  };

  return (
    <div className="inline-block" ref={sortRef}>
      <button
        onClick={handleApplySort}
        onMouseEnter={onMouseEnter}
        onMouseLeave={onMouseLeave}
        className="text-[10px] sm:text-[10px] font-bold uppercase tracking-wider text-black hover:text-gray-600 flex items-center relative py-1"
      >
        <span className="px-1">Sort by: {getSortOptionText()}</span>
        <svg
          xmlns="http://www.w3.org/2000/svg"
          className="h-2 w-2 sm:h-3 sm:w-3 ml-1"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
        </svg>
        <div className="absolute bottom-[-2px] left-0 right-0 h-[1px] overflow-hidden">
          <motion.div
            className="absolute inset-0 bg-black"
            initial={{ width: "100%", left: 0 }}
            animate={{ width: "100%", left: 0 }}
            transition={{ duration: 0 }}
          />
        </div>
      </button>

      {isSortOpen && (
        <motion.div
          initial={{ opacity: 0, y: -10 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: -10 }}
          transition={{ duration: 0.3 }}
          className="absolute right-0 mt-1 bg-white border border-gray-200 shadow-md z-10 min-w-[200px]"
        >
          <ul className="py-2">
            {sortOptions.map((option) => (
              <li key={option.value}>
                <button
                  onClick={() => {
                    handleSortChange(option.value);
                    handleCloseSort();
                  }}
                  className={`w-full text-left px-4 py-2 text-sm font-medium ${
                    selectedSort === option.value ? "bg-black text-white" : "text-black hover:bg-gray-100"
                  }`}
                >
                  {option.label}
                </button>
              </li>
            ))}
          </ul>
        </motion.div>
      )}
    </div>
  );
}"],"names":[],"mappings":";;;;AAAA;;;AAsBe,SAAS,YAAY,EAClC,UAAU,EACV,OAAO,EACP,YAAY,EACZ,gBAAgB,EAChB,eAAe,EACf,eAAe,EACf,cAAc,EAAE,EAChB,aAAa,EACb,YAAY,EACZ,YAAY,EACZ,YAAY,EACK;IACjB,MAAM,oBAAoB;QACxB,IAAI,CAAC,eAAe,YAAY,MAAM,KAAK,GAAG,OAAO;QACrD,MAAM,QAAQ,YAAY,IAAI,CAAC,CAAC,MAAQ,IAAI,KAAK,KAAK;QACtD,OAAO,QAAQ,MAAM,KAAK,GAAG;IAC/B;IAEA,qBACE,6LAAC;QAAI,WAAU;QAAe,KAAK;;0BACjC,6LAAC;gBACC,SAAS;gBACT,cAAc;gBACd,cAAc;gBACd,WAAU;;kCAEV,6LAAC;wBAAK,WAAU;;4BAAO;4BAAU;;;;;;;kCACjC,6LAAC;wBACC,OAAM;wBACN,WAAU;wBACV,MAAK;wBACL,SAAQ;wBACR,QAAO;kCAEP,cAAA,6LAAC;4BAAK,eAAc;4BAAQ,gBAAe;4BAAQ,aAAa;4BAAG,GAAE;;;;;;;;;;;kCAEvE,6LAAC;wBAAI,WAAU;kCACb,cAAA,6LAAC,6LAAA,CAAA,SAAM,CAAC,GAAG;4BACT,WAAU;4BACV,SAAS;gCAAE,OAAO;gCAAQ,MAAM;4BAAE;4BAClC,SAAS;gCAAE,OAAO;gCAAQ,MAAM;4BAAE;4BAClC,YAAY;gCAAE,UAAU;4BAAE;;;;;;;;;;;;;;;;;YAK/B,4BACC,6LAAC,6LAAA,CAAA,SAAM,CAAC,GAAG;gBACT,SAAS;oBAAE,SAAS;oBAAG,GAAG,CAAC;gBAAG;gBAC9B,SAAS;oBAAE,SAAS;oBAAG,GAAG;gBAAE;gBAC5B,MAAM;oBAAE,SAAS;oBAAG,GAAG,CAAC;gBAAG;gBAC3B,YAAY;oBAAE,UAAU;gBAAI;gBAC5B,WAAU;0BAEV,cAAA,6LAAC;oBAAG,WAAU;8BACX,YAAY,GAAG,CAAC,CAAC,uBAChB,6LAAC;sCACC,cAAA,6LAAC;gCACC,SAAS;oCACP,iBAAiB,OAAO,KAAK;oCAC7B;gCACF;gCACA,WAAW,CAAC,+CAA+C,EACzD,iBAAiB,OAAO,KAAK,GAAG,wBAAwB,gCACxD;0CAED,OAAO,KAAK;;;;;;2BAVR,OAAO,KAAK;;;;;;;;;;;;;;;;;;;;;AAmBnC;KA5EwB","debugId":null}},
