    {"offset": {"line": 121, "column": 0}, "map": {"version":3,"sources":["file://C%3A/Users/hongh/aishh/src/components/OrderConfirmationModal.js"],"sourcesContent":["import { useOrders } from '../context/OrderContext';
import { motion } from "framer-motion";
import { useRouter } from 'next/navigation';

export default function OrderConfirmationModal({ isOpen, onClose, orderDetails }) {
  const { addOrder } = useOrders();
  const router = useRouter();

  if (!isOpen || !orderDetails) return null;

  const handleContinueShopping = () => {
    if (orderDetails) {
      const orderWithDate = {
        ...orderDetails,
        orderDate: Date.now(),
        status: 'processing'
      };
      addOrder(orderWithDate);
    }
    onClose();
    // Chuyển hướng đến trang xác nhận đơn hàng thành công
    router.push(`/order-success/${orderDetails.orderCode}`);
  };

  return (
    <div className="fixed inset-0 backdrop-blur-sm bg-black/10 flex items-center justify-center z-50">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="bg-white/80 w-11/12 max-w-md relative"
      >
        <button
          onClick={onClose}
          className="absolute top-4 right-4 text-black"
        >
          <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M18 6L6 18" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            <path d="M6 6L18 18" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
        </button>

        <div className="text-center py-8 px-6">
          <h2 style={{ 
            fontSize: "1.2em", 
            color: "#000000", 
            textTransform: "uppercase", 
            letterSpacing: "0.1em", 
            marginBottom: "20px",
            fontWeight: "bold"
          }}>
            XÁC NHẬN ĐƠN HÀNG
          </h2>

          {/* Thông tin sản phẩm */}
          <div style={{ marginBottom: "20px" }}>
            {orderDetails.items.map((item, index) => (
              <div key={index} style={{ 
                display: "flex", 
                alignItems: "center", 
                marginBottom: "15px",
                padding: "10px",
                backgroundColor: "#f8f8f8",
                borderRadius: "4px"
              }}>
                <img 
                  src={item.image} 
                  alt={item.name} 
                  style={{
                    width: "80px",
                    height: "80px",
                    objectFit: "cover",
                    marginRight: "15px"
                  }}
                />
                <div style={{ textAlign: "left" }}>
                  <p style={{ 
                    fontSize: "0.9em", 
                    fontWeight: "bold",
                    marginBottom: "4px" 
                  }}>{item.name}</p>
                  <p style={{ fontSize: "0.8em" }}>Size: {item.variation}</p>
                  <p style={{ fontSize: "0.8em" }}>Số lượng: {item.quantity}</p>
                  <p style={{ fontSize: "0.8em" }}>Giá: {item.price}</p>
                </div>
              </div>
            ))}
          </div>

          <div style={{ fontSize: "0.9em", color: "#000000", marginBottom: "20px" }}>
            <p style={{ marginBottom: "8px" }}>Mã đơn hàng: {orderDetails.orderCode}</p>
            <p style={{ marginBottom: "8px" }}>Họ tên: {orderDetails.fullName}</p>
            <p style={{ marginBottom: "8px" }}>Email: {orderDetails.email}</p>
            <p style={{ marginBottom: "8px" }}>Số điện thoại: {orderDetails.phone}</p>
            {orderDetails.additionalPhone && (
              <p style={{ marginBottom: "8px" }}>Số điện thoại phụ: {orderDetails.additionalPhone}</p>
            )}
            <p style={{ marginBottom: "8px" }}>
              Địa chỉ: {orderDetails.address}, {orderDetails.ward}, {orderDetails.district}, {orderDetails.province}
            </p>
            <p style={{ marginBottom: "8px" }}>Phương thức thanh toán: {
              orderDetails.paymentMethod === 'cod' ? 'Thanh toán khi nhận hàng' : 'Chuyển khoản ngân hàng'
            }</p>
            <p style={{ 
              marginBottom: "8px",
              fontSize: "1em",
              fontWeight: "bold"
            }}>Tổng tiền: {orderDetails.total}</p>
          </div>

          {orderDetails.paymentMethod === 'bank' && (
            <div style={{
              backgroundColor: "#f8f8f8",
              padding: "15px",
              marginBottom: "20px",
              borderRadius: "4px"
            }}>
              <p style={{ fontSize: "0.9em", color: "#000000", fontStyle: "italic" }}>
                Vui lòng chuyển khoản theo thông tin đã cung cấp và gửi biên lai về Facebook hoặc Instagram AISH
              </p>
            </div>
          )}
        </div>

        <div className="border-t border-gray-200">
          <button
            onClick={handleContinueShopping}
            style={{
              width: "100%",
              padding: "12px",
              fontSize: "0.7em",
              color: "#ffffff",
              backgroundColor: "#000000",
              border: "none",
              cursor: "pointer",
              textTransform: "uppercase",
              letterSpacing: "0.1em"
            }}
          >
            Xác nhận
          </button>
        </div>
      </motion.div>
    </div>
  );
} "],"names":[],"mappings":";;;;AAAA;AACA;AACA;;;;;;AAEe,SAAS,uBAAuB,EAAE,MAAM,EAAE,OAAO,EAAE,YAAY,EAAE;;IAC9E,MAAM,EAAE,QAAQ,EAAE,GAAG,CAAA,GAAA,iIAAA,CAAA,YAAS,AAAD;IAC7B,MAAM,SAAS,CAAA,GAAA,qIAAA,CAAA,YAAS,AAAD;IAEvB,IAAI,CAAC,UAAU,CAAC,cAAc,OAAO;IAErC,MAAM,yBAAyB;QAC7B,IAAI,cAAc;YAChB,MAAM,gBAAgB;gBACpB,GAAG,YAAY;gBACf,WAAW,KAAK,GAAG;gBACnB,QAAQ;YACV;YACA,SAAS;QACX;QACA;QACA,sDAAsD;QACtD,OAAO,IAAI,CAAC,CAAC,eAAe,EAAE,aAAa,SAAS,EAAE;IACxD;IAEA,qBACE,6LAAC;QAAI,WAAU;kBACb,cAAA,6LAAC,6LAAA,CAAA,SAAM,CAAC,GAAG;YACT,SAAS;gBAAE,SAAS;gBAAG,GAAG;YAAG;YAC7B,SAAS;gBAAE,SAAS;gBAAG,GAAG;YAAE;YAC5B,WAAU;;8BAEV,6LAAC;oBACC,SAAS;oBACT,WAAU;8BAEV,cAAA,6LAAC;wBAAI,OAAM;wBAAK,QAAO;wBAAK,SAAQ;wBAAY,MAAK;wBAAO,OAAM;;0CAChE,6LAAC;gCAAK,GAAE;gCAAa,QAAO;gCAAe,aAAY;gCAAI,eAAc;gCAAQ,gBAAe;;;;;;0CAChG,6LAAC;gCAAK,GAAE;gCAAa,QAAO;gCAAe,aAAY;gCAAI,eAAc;gCAAQ,gBAAe;;;;;;;;;;;;;;;;;8BAIpG,6LAAC;oBAAI,WAAU;;sCACb,6LAAC;4BAAG,OAAO;gCACT,UAAU;gCACV,OAAO;gCACP,eAAe;gCACf,eAAe;gCACf,cAAc;gCACd,YAAY;4BACd;sCAAG;;;;;;sCAKH,6LAAC;4BAAI,OAAO;gCAAE,cAAc;4BAAO;sCAChC,aAAa,KAAK,CAAC,GAAG,CAAC,CAAC,MAAM,sBAC7B,6LAAC;oCAAgB,OAAO;wCACtB,SAAS;wCACT,YAAY;wCACZ,cAAc;wCACd,SAAS;wCACT,iBAAiB;wCACjB,cAAc;oCAChB;;sDACE,6LAAC;4CACC,KAAK,KAAK,KAAK;4CACf,KAAK,KAAK,IAAI;4CACd,OAAO;gDACL,OAAO;gDACP,QAAQ;gDACR,WAAW;gDACX,aAAa;4CACf;;;;;;sDAEF,6LAAC;4CAAI,OAAO;gDAAE,WAAW;4CAAO;;8DAC9B,6LAAC;oDAAE,OAAO;wDACR,UAAU;wDACV,YAAY;wDACZ,cAAc;oDAChB;8DAAI,KAAK,IAAI;;;;;;8DACb,6LAAC;oDAAE,OAAO;wDAAE,UAAU;oDAAQ;;wDAAG;wDAAO,KAAK,SAAS;;;;;;;8DACtD,6LAAC;oDAAE,OAAO;wDAAE,UAAU;oDAAQ;;wDAAG;wDAAW,KAAK,QAAQ;;;;;;;8DACzD,6LAAC;oDAAE,OAAO;wDAAE,UAAU;oDAAQ;;wDAAG;wDAAM,KAAK,KAAK;;;;;;;;;;;;;;mCA1B3C;;;;;;;;;;sCAgCd,6LAAC;4BAAI,OAAO;gCAAE,UAAU;gCAAS,OAAO;gCAAW,cAAc;4BAAO;;8CACtE,6LAAC;oCAAE,OAAO;wCAAE,cAAc;oCAAM;;wCAAG;wCAAc,aAAa,SAAS;;;;;;;8CACvE,6LAAC;oCAAE,OAAO;wCAAE,cAAc;oCAAM;;wCAAG;wCAAS,aAAa,QAAQ;;;;;;;8CACjE,6LAAC;oCAAE,OAAO;wCAAE,cAAc;oCAAM;;wCAAG;wCAAQ,aAAa,KAAK;;;;;;;8CAC7D,6LAAC;oCAAE,OAAO;wCAAE,cAAc;oCAAM;;wCAAG;wCAAgB,aAAa,KAAK;;;;;;;gCACpE,aAAa,eAAe,kBAC3B,6LAAC;oCAAE,OAAO;wCAAE,cAAc;oCAAM;;wCAAG;wCAAoB,aAAa,eAAe;;;;;;;8CAErF,6LAAC;oCAAE,OAAO;wCAAE,cAAc;oCAAM;;wCAAG;wCACvB,aAAa,OAAO;wCAAC;wCAAG,aAAa,IAAI;wCAAC;wCAAG,aAAa,QAAQ;wCAAC;wCAAG,aAAa,QAAQ;;;;;;;8CAEvG,6LAAC;oCAAE,OAAO;wCAAE,cAAc;oCAAM;;wCAAG;wCACjC,aAAa,aAAa,KAAK,QAAQ,6BAA6B;;;;;;;8CAEtE,6LAAC;oCAAE,OAAO;wCACR,cAAc;wCACd,UAAU;wCACV,YAAY;oCACd;;wCAAG;wCAAY,aAAa,KAAK;;;;;;;;;;;;;wBAGlC,aAAa,aAAa,KAAK,wBAC9B,6LAAC;4BAAI,OAAO;gCACV,iBAAiB;gCACjB,SAAS;gCACT,cAAc;gCACd,cAAc;4BAChB;sCACE,cAAA,6LAAC;gCAAE,OAAO;oCAAE,UAAU;oCAAS,OAAO;oCAAW,WAAW;gCAAS;0CAAG;;;;;;;;;;;;;;;;;8BAO9E,6LAAC;oBAAI,WAAU;8BACb,cAAA,6LAAC;wBACC,SAAS;wBACT,OAAO;4BACL,OAAO;4BACP,SAAS;4BACT,UAAU;4BACV,OAAO;4BACP,iBAAiB;4BACjB,QAAQ;4BACR,QAAQ;4BACR,eAAe;4BACf,eAAe;wBACjB;kCACD;;;;;;;;;;;;;;;;;;;;;;AAOX;GA5IwB;;QACD,iIAAA,CAAA,YAAS;QACf,qIAAA,CAAA,YAAS;;;KAFF","debugId":null}},
    {"offset": {"line": 537, "column": 0}, "map": {"version":3,"sources":["file://C%3A/Users/hongh/aishh/src/app/checkout/shipping/page.js"],"sourcesContent":[""use client";
import { useState } from "react";
import Link from "next/link";
import { useVietnameseAddress } from "../../../hooks/useVietnameseAddress";
import OrderConfirmationModal from "../../../components/OrderConfirmationModal";

export default function ShippingPage({
  userEmail,
  isShippingComplete,
  showAdditionalContact,
  showPaymentDetails,
  errors,
  handleShippingComplete,
  handleAdditionalContactToggle,
  order,
}) {
  const [paymentMethod, setPaymentMethod] = useState("");
  const [showPaymentSection, setShowPaymentSection] = useState(false);
  const [showConfirmationModal, setShowConfirmationModal] = useState(false);
  const [orderDetails, setOrderDetails] = useState(null);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    address: "",
    phone: "",
    additionalPhone: "",
  });

  const {
    provinces,
    districts,
    wards,
    selectedProvince,
    selectedDistrict,
    selectedWard,
    handleProvinceChange,
    handleDistrictChange,
    handleWardChange
  } = useVietnameseAddress();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleContinueToPayment = (e) => {
    e.preventDefault();
    // Thêm validation cho form shipping
    const shippingForm = document.getElementById('shippingForm');
    if (shippingForm.checkValidity()) {
      setShowPaymentSection(true);
      // Scroll to payment section
      const paymentSection = document.getElementById('paymentSection');
      if (paymentSection) {
        paymentSection.scrollIntoView({ behavior: 'smooth' });
      }
    } else {
      shippingForm.reportValidity();
    }
  };

  const handleCompleteOrder = async (e) => {
    e.preventDefault();
    
    try {
      // Kiểm tra xem đã chọn đủ thông tin địa chỉ chưa
      if (!selectedProvince || !selectedDistrict || !selectedWard) {
        alert("Vui lòng chọn đầy đủ thông tin địa chỉ");
        return;
      }

      // Kiểm tra các trường bắt buộc
      if (!formData.firstName || !formData.lastName || !formData.address || !formData.phone) {
        alert("Vui lòng điền đầy đủ thông tin bắt buộc");
        return;
      }

      const selectedProvinceData = provinces.find(p => p.code === selectedProvince);
      const selectedDistrictData = districts.find(d => d.code === selectedDistrict);
      const selectedWardData = wards.find(w => w.code === selectedWard);

      const details = {
        orderCode: `ORDER${Date.now()}`,
        fullName: `${formData.firstName} ${formData.lastName}`,
        email: userEmail,
        phone: formData.phone,
        additionalPhone: formData.additionalPhone || null,
        address: formData.address,
        ward: selectedWardData?.name || "",
        district: selectedDistrictData?.name || "",
        province: selectedProvinceData?.name || "",
        items: order.items || [],
        subtotal: order.subtotal || "0",
        shippingFee: "Miễn phí",
        total: order.total || "0",
        paymentMethod: paymentMethod
      };

      // Gửi đơn hàng lên server
      const response = await fetch('/api/orders', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(details),
      });

      const data = await response.json();

      if (data.ok) {
        setOrderDetails(details);
        setShowConfirmationModal(true);
      } else {
        throw new Error(data.message);
      }
    } catch (error) {
      console.error("Error creating order:", error);
      alert("Có lỗi xảy ra khi tạo đơn hàng. Vui lòng thử lại.");
    }
  };

  const handlePaymentMethodChange = (method) => {
    setPaymentMethod(method);
  };

  const renderPaymentInfo = () => {
    if (!paymentMethod) return null;

    if (paymentMethod === 'cod') {
      return (
        <div style={{
          marginTop: "20px",
          padding: "15px",
          backgroundColor: "#f8f8f8",
          borderRadius: "4px"
        }}>
          <p style={{
            fontSize: "0.9em",
            color: "#000000",
            fontStyle: "italic"
          }}>
            Quý khách vui lòng để ý mã đơn hàng để tiện theo dõi.
          </p>
        </div>
      );
    }

    if (paymentMethod === 'bank') {
      return (
        <div style={{
          marginTop: "20px",
          padding: "15px",
          backgroundColor: "#f8f8f8",
          borderRadius: "4px"
        }}>
          <h3 style={{
            fontSize: "0.9em",
            fontWeight: "bold",
            marginBottom: "10px",
            color: "#000000"
          }}>
            Thông tin chuyển khoản
          </h3>
          <div style={{ fontSize: "0.9em", color: "#000000" }}>
            <p style={{ marginBottom: "8px" }}>Ngân hàng: Vietcombank</p>
            <p style={{ marginBottom: "8px" }}>Chủ tài khoản: AISH COMPANY</p>
            <p style={{ marginBottom: "8px" }}>Số tài khoản: 1234567890</p>
            <p style={{ marginBottom: "8px" }}>
              Nội dung chuyển khoản: {order.orderNumber || "ORDER123"} + {order.phone || "SĐT"}
            </p>
            <p style={{
              marginTop: "15px",
              fontStyle: "italic",
              color: "#666666"
            }}>
              Quý khách vui lòng chụp màn hình gửi về Facebook hoặc Instagram AISH
            </p>
          </div>
        </div>
      );
    }
  };

  return (
    <div style={styles.leftSection}>
      <div style={styles.section}>
        <h2 style={styles.sectionTitle}>THÔNG TIN LIÊN HỆ</h2>
        <p style={styles.sectionText}>
          {userEmail} (<Link href="/logout" style={styles.link}>Đăng xuất</Link>)
        </p>
      </div>

      <div style={styles.section}>
        <h2 style={styles.sectionTitle}>ĐỊA CHỈ GIAO HÀNG</h2>
        <form id="shippingForm" style={styles.form}>
          <div style={styles.formRow}>
            <div style={styles.formGroup}>
              <label style={styles.label}>Họ *</label>
              <input
                name="firstName"
                type="text"
                value={formData.firstName}
                onChange={handleInputChange}
                style={{ ...styles.input, borderColor: errors.firstName ? "red" : "#e0e0e0" }}
                required
              />
            </div>
            <div style={styles.formGroup}>
              <label style={styles.label}>Tên *</label>
              <input
                name="lastName"
                type="text"
                value={formData.lastName}
                onChange={handleInputChange}
                style={{ ...styles.input, borderColor: errors.lastName ? "red" : "#e0e0e0" }}
                required
              />
            </div>
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>Địa chỉ *</label>
            <input
              name="address"
              type="text"
              value={formData.address}
              onChange={handleInputChange}
              style={{ ...styles.input, borderColor: errors.address ? "red" : "#e0e0e0" }}
              required
            />
          </div>

          <div style={styles.formGroup}>
            <label style={styles.label}>Căn hộ, tòa nhà, v.v. (không bắt buộc)</label>
            <input
              name="apartment"
              type="text"
              style={styles.input}
            />
          </div>

          <div style={styles.formRow}>
            <div style={styles.formGroup}>
              <label style={styles.label}>Tỉnh/Thành phố *</label>
              <select
                value={selectedProvince}
                onChange={handleProvinceChange}
                style={{ ...styles.input, borderColor: errors.province ? "red" : "#e0e0e0" }}
                required
              >
                <option value="">Chọn Tỉnh/Thành phố</option>
                {provinces.map((province) => (
                  <option key={province.code} value={province.code}>
                    {province.name}
                  </option>
                ))}
              </select>
            </div>
            <div style={styles.formGroup}>
              <label style={styles.label}>Quận/Huyện *</label>
              <select
                value={selectedDistrict}
                onChange={handleDistrictChange}
                style={{ ...styles.input, borderColor: errors.district ? "red" : "#e0e0e0" }}
                required
                disabled={!selectedProvince}
              >
                <option value="">Chọn Quận/Huyện</option>
                {districts.map((district) => (
                  <option key={district.code} value={district.code}>
                    {district.name}
                  </option>
                ))}
              </select>
            </div>
          </div>

          <div style={styles.formRow}>
            <div style={styles.formGroup}>
              <label style={styles.label}>Phường/Xã *</label>
              <select
                value={selectedWard}
                onChange={handleWardChange}
                style={{ ...styles.input, borderColor: errors.ward ? "red" : "#e0e0e0" }}
                required
                disabled={!selectedDistrict}
              >
                <option value="">Chọn Phường/Xã</option>
                {wards.map((ward) => (
                  <option key={ward.code} value={ward.code}>
                    {ward.name}
                  </option>
                ))}
              </select>
            </div>
            <div style={styles.formGroup}>
              <label style={styles.label}>Số điện thoại *</label>
              <input
                name="phone"
                type="tel"
                value={formData.phone}
                onChange={handleInputChange}
                style={{ ...styles.input, borderColor: errors.phone ? "red" : "#e0e0e0" }}
                required
              />
            </div>
          </div>

          {showAdditionalContact && (
            <div style={styles.formGroup}>
              <label style={styles.label}>Số điện thoại phụ (không bắt buộc)</label>
              <input
                name="additionalPhone"
                type="tel"
                value={formData.additionalPhone}
                onChange={handleInputChange}
                style={styles.input}
              />
            </div>
          )}

          <button
            onClick={handleContinueToPayment}
            style={styles.continueButton}
            type="submit"
          >
            Tiếp tục thanh toán
          </button>
        </form>
      </div>

      {showPaymentSection && (
        <div id="paymentSection" style={styles.section}>
          <h2 style={styles.sectionTitle}>HÌNH THỨC THANH TOÁN</h2>
          <div style={styles.paymentMethods}>
            <div
              style={{
                ...styles.paymentMethod,
                borderColor: paymentMethod === "cod" ? "#000" : "#e0e0e0",
              }}
              onClick={() => handlePaymentMethodChange("cod")}
            >
              <div style={styles.paymentMethodHeader}>
                <input
                  type="radio"
                  name="paymentMethod"
                  checked={paymentMethod === "cod"}
                  onChange={() => handlePaymentMethodChange("cod")}
                  style={styles.radio}
                />
                <label style={styles.paymentMethodLabel}>
                  Thanh toán khi nhận hàng (COD)
                </label>
              </div>
            </div>

            <div
              style={{
                ...styles.paymentMethod,
                borderColor: paymentMethod === "bank" ? "#000" : "#e0e0e0",
              }}
              onClick={() => handlePaymentMethodChange("bank")}
            >
              <div style={styles.paymentMethodHeader}>
                <input
                  type="radio"
                  name="paymentMethod"
                  checked={paymentMethod === "bank"}
                  onChange={() => handlePaymentMethodChange("bank")}
                  style={styles.radio}
                />
                <label style={styles.paymentMethodLabel}>
                  Chuyển khoản ngân hàng
                </label>
              </div>
            </div>
          </div>

          {renderPaymentInfo()}

          {paymentMethod && (
            <button
              onClick={handleCompleteOrder}
              style={styles.completeOrderButton}
            >
              Hoàn tất đơn hàng
            </button>
          )}
        </div>
      )}

      <OrderConfirmationModal
        isOpen={showConfirmationModal}
        onClose={() => setShowConfirmationModal(false)}
        orderDetails={orderDetails}
      />
    </div>
  );
}

