  radio: {
    width: "18px",
    height: "18px",
    cursor: "pointer",
  },
  bankInfo: {
    fontWeight: "500",
    marginBottom: "10px",
  },
  completeOrderButton: {
    width: "100%",
    padding: "15px",
    backgroundColor: "#000",
