    {"offset": {"line": 7, "column": 0}, "map": {"version":3,"sources":["file://C%3A/Users/hongh/aishh/src/app/products/%5Bid%5D/page.tsx"],"sourcesContent":[""use client";

import { useState, useEffect } from "react";
import { useParams, usePathname } from "next/navigation";
import { motion } from "framer-motion";
import { toast } from "react-hot-toast";
import { useCart } from "@/context/CartContext";
import { Product } from "@/types/product";
import Image from "next/image";
import Link from "next/link";
import HelpPanel from "@/components/HelpPanel";

export default function ProductDetailPage() {
  const { id } = useParams();
  const pathname = usePathname();
  const [product, setProduct] = useState<Product | null>(null);
  const [selectedSize, setSelectedSize] = useState<string | undefined>(undefined);
  const [quantity, setQuantity] = useState<number>(1);
  const [isLoading, setIsLoading] = useState(true);
  const { addToCart } = useCart();
  const [showFullImage, setShowFullImage] = useState(false);
  const [showHelpPanel, setShowHelpPanel] = useState(false);
  const [showSizeOptions, setShowSizeOptions] = useState(false);
  const [showSizeGuide, setShowSizeGuide] = useState(false);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await fetch(`/api/products/${id}`);
        const data = await response.json();
        if (data.ok) {
          setProduct(data.product);
        }
      } catch (error) {
        console.error("Error fetching product:", error);
        toast.error("Failed to load product");
      } finally {
        setIsLoading(false);
      }
    };

    fetchProduct();
  }, [id]);

  if (isLoading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-gray-900"></div>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <p className="text-red-500">Product not found</p>
      </div>
    );
  }

  const handleAddToCart = () => {
    if (!selectedSize) {
      toast.error("Please select a size");
      return;
    }
    addToCart({
      id: product._id,
      name: product.name,
      price: product.price,
      image: product.images[0],
      size: selectedSize,
      quantity: quantity,
      colors: product.colors,
    });
    toast.success("Added to cart");
  };

  const handleQuantityChange = (value: number) => {
    if (!product || !selectedSize) return;
    
    const sizeQuantity = product[`quantity${selectedSize}` as keyof Product] as number;
    if (value > sizeQuantity) {
      toast.error(`Chỉ còn ${sizeQuantity} sản phẩm size ${selectedSize}`);
      return;
    }
    
    setQuantity(value);
  };

  const handlePrevImage = () => {
    if (!product || !product.images) return;
    setCurrentImageIndex((currentImageIndex - 1 + product.images.length) % product.images.length);
  };

  const handleNextImage = () => {
    if (!product || !product.images) return;
    setCurrentImageIndex((currentImageIndex + 1) % product.images.length);
  };

  const isClothing = product.category && product.category !== "CAP";

  return (
    <div className="min-h-screen bg-white pt-20">
      <div className="container mx-auto px-4 py-8">
        <div className="flex flex-col lg:flex-row gap-8">
          {/* Product Images */}
          <div className="lg:w-1/2">
            <div className="relative aspect-square mb-4">
              <Image
                src={product.images[currentImageIndex]}
                alt={product.name}
                fill
                className="object-cover cursor-pointer"
                onClick={() => setShowFullImage(true)}
              />
            </div>
            <div className="grid grid-cols-4 gap-2">
              {product.images.map((image, index) => (
                <div
                  key={index}
                  className={`relative aspect-square border-2 cursor-pointer ${
                    currentImageIndex === index ? "border-black" : "border-gray-200"
                  }`}
                  onClick={() => setCurrentImageIndex(index)}
                >
                  <Image
                    src={image}
                    alt={`${product.name} - Image ${index + 1}`}
                    fill
                    className="object-cover"
                  />
                </div>
              ))}
            </div>
          </div>

          {/* Product Info */}
          <div className="lg:w-1/2">
            <h1 className="text-2xl font-bold mb-4">{product.name}</h1>
            <p className="text-xl font-semibold mb-4">${product.price.toFixed(2)}</p>
            <p className="text-gray-600 mb-6">{product.description}</p>

            {/* Size Selection */}
            <div className="mb-6">
              <div className="flex justify-between items-center mb-2">
                <label className="font-semibold">Size</label>
                <button
                  className="text-sm text-gray-600 underline"
                  onClick={() => setShowSizeGuide(true)}
                >
                  Size Guide
                </button>
              </div>
              <div className="grid grid-cols-3 gap-2">
                {product.sizes.map((size) => (
                  <button
                    key={size}
                    className={`py-2 border ${
                      selectedSize === size
                        ? "border-black bg-black text-white"
                        : "border-gray-300 hover:border-black"
                    }`}
                    onClick={() => setSelectedSize(size)}
                  >
                    {size}
                  </button>
                ))}
              </div>
            </div>

            {/* Quantity */}
            <div className="mb-6">
              <label className="font-semibold block mb-2">Quantity</label>
              <div className="flex items-center gap-2">
                <button
                  className="w-8 h-8 border border-gray-300 flex items-center justify-center"
                  onClick={() => setQuantity(Math.max(1, quantity - 1))}
                >
                  -
                </button>
                <span className="w-12 text-center">{quantity}</span>
                <button
                  className="w-8 h-8 border border-gray-300 flex items-center justify-center"
                  onClick={() => setQuantity(quantity + 1)}
                >
                  +
                </button>
              </div>
            </div>

            {/* Add to Cart Button */}
            <button
              className="w-full bg-black text-white py-3 font-semibold hover:bg-gray-800 transition-colors"
              onClick={handleAddToCart}
            >
              Add to Cart
            </button>
          </div>
        </div>
      </div>

      {/* Full Screen Image Modal */}
      {showFullImage && (
        <div
          className="fixed inset-0 bg-black bg-opacity-90 z-50 flex items-center justify-center"
          onClick={() => setShowFullImage(false)}
        >
          <div className="relative w-full h-full max-w-4xl max-h-[90vh] p-4">
            <Image
              src={product.images[currentImageIndex]}
              alt={product.name}
              fill
              className="object-contain"
            />
            <button
              className="absolute top-4 right-4 text-white text-2xl"
              onClick={() => setShowFullImage(false)}
            >
              ✕
            </button>
          </div>
        </div>
      )}

      {/* Size Guide Modal */}
      {showSizeGuide && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center"
          onClick={() => setShowSizeGuide(false)}
        >
          <div className="bg-white p-6 max-w-lg w-full mx-4">
            <h2 className="text-xl font-bold mb-4">Size Guide</h2>
            <div className="overflow-x-auto">
              <table className="w-full border-collapse">
                <thead>
                  <tr>
                    <th className="border p-2">Size</th>
                    <th className="border p-2">Chest (cm)</th>
                    <th className="border p-2">Waist (cm)</th>
                    <th className="border p-2">Hip (cm)</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td className="border p-2">M</td>
                    <td className="border p-2">96-101</td>
                    <td className="border p-2">81-86</td>
                    <td className="border p-2">91-96</td>
                  </tr>
                  <tr>
                    <td className="border p-2">L</td>
                    <td className="border p-2">101-106</td>
                    <td className="border p-2">86-91</td>
                    <td className="border p-2">96-101</td>
                  </tr>
                  <tr>
                    <td className="border p-2">XL</td>
                    <td className="border p-2">106-111</td>
                    <td className="border p-2">91-96</td>
                    <td className="border p-2">101-106</td>
                  </tr>
                </tbody>
              </table>
            </div>
            <button
              className="mt-4 w-full bg-black text-white py-2"
              onClick={() => setShowSizeGuide(false)}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
} "],"names":[],"mappings":";;;;AAEA;AACA;AAEA;AACA;AAEA;;;AARA;;;;;;AAYe,SAAS;;IACtB,MAAM,EAAE,EAAE,EAAE,GAAG,CAAA,GAAA,qIAAA,CAAA,YAAS,AAAD;IACvB,MAAM,WAAW,CAAA,GAAA,qIAAA,CAAA,cAAW,AAAD;IAC3B,MAAM,CAAC,SAAS,WAAW,GAAG,CAAA,GAAA,6JAAA,CAAA,WAAQ,AAAD,EAAkB;IACvD,MAAM,CAAC,cAAc,gBAAgB,GAAG,CAAA,GAAA,6JAAA,CAAA,WAAQ,AAAD,EAAsB;IACrE,MAAM,CAAC,UAAU,YAAY,GAAG,CAAA,GAAA,6JAAA,CAAA,WAAQ,AAAD,EAAU;IACjD,MAAM,CAAC,WAAW,aAAa,GAAG,CAAA,GAAA,6JAAA,CAAA,WAAQ,AAAD,EAAE;IAC3C,MAAM,EAAE,SAAS,EAAE,GAAG,CAAA,GAAA,gIAAA,CAAA,UAAO,AAAD;IAC5B,MAAM,CAAC,eAAe,iBAAiB,GAAG,CAAA,GAAA,6JAAA,CAAA,WAAQ,AAAD,EAAE;IACnD,MAAM,CAAC,eAAe,iBAAiB,GAAG,CAAA,GAAA,6JAAA,CAAA,WAAQ,AAAD,EAAE;IACnD,MAAM,CAAC,iBAAiB,mBAAmB,GAAG,CAAA,GAAA,6JAAA,CAAA,WAAQ,AAAD,EAAE;IACvD,MAAM,CAAC,eAAe,iBAAiB,GAAG,CAAA,GAAA,6JAAA,CAAA,WAAQ,AAAD,EAAE;IACnD,MAAM,CAAC,mBAAmB,qBAAqB,GAAG,CAAA,GAAA,6JAAA,CAAA,WAAQ,AAAD,EAAE;IAE3D,CAAA,GAAA,6JAAA,CAAA,YAAS,AAAD;uCAAE;YACR,MAAM;4DAAe;oBACnB,IAAI;wBACF,MAAM,WAAW,MAAM,MAAM,CAAC,cAAc,EAAE,IAAI;wBAClD,MAAM,OAAO,MAAM,SAAS,IAAI;wBAChC,IAAI,KAAK,EAAE,EAAE;4BACX,WAAW,KAAK,OAAO;wBACzB;oBACF,EAAE,OAAO,OAAO;wBACd,QAAQ,KAAK,CAAC,2BAA2B;wBACzC,0JAAA,CAAA,QAAK,CAAC,KAAK,CAAC;oBACd,SAAU;wBACR,aAAa;oBACf;gBACF;;YAEA;QACF;sCAAG;QAAC;KAAG;IAEP,IAAI,WAAW;QACb,qBACE,6LAAC;YAAI,WAAU;sBACb,cAAA,6LAAC;gBAAI,WAAU;;;;;;;;;;;IAGrB;IAEA,IAAI,CAAC,SAAS;QACZ,qBACE,6LAAC;YAAI,WAAU;sBACb,cAAA,6LAAC;gBAAE,WAAU;0BAAe;;;;;;;;;;;IAGlC;IAEA,MAAM,kBAAkB;QACtB,IAAI,CAAC,cAAc;YACjB,0JAAA,CAAA,QAAK,CAAC,KAAK,CAAC;YACZ;QACF;QACA,UAAU;YACR,IAAI,QAAQ,GAAG;YACf,MAAM,QAAQ,IAAI;YAClB,OAAO,QAAQ,KAAK;YACpB,OAAO,QAAQ,MAAM,CAAC,EAAE;YACxB,MAAM;YACN,UAAU;YACV,QAAQ,QAAQ,MAAM;QACxB;QACA,0JAAA,CAAA,QAAK,CAAC,OAAO,CAAC;IAChB;IAEA,MAAM,uBAAuB,CAAC;QAC5B,IAAI,CAAC,WAAW,CAAC,cAAc;QAE/B,MAAM,eAAe,OAAO,CAAC,CAAC,QAAQ,EAAE,cAAc,CAAkB;QACxE,IAAI,QAAQ,cAAc;YACxB,0JAAA,CAAA,QAAK,CAAC,KAAK,CAAC,CAAC,QAAQ,EAAE,aAAa,eAAe,EAAE,cAAc;YACnE;QACF;QAEA,YAAY;IACd;IAEA,MAAM,kBAAkB;QACtB,IAAI,CAAC,WAAW,CAAC,QAAQ,MAAM,EAAE;QACjC,qBAAqB,CAAC,oBAAoB,IAAI,QAAQ,