import { NextResponse } from "next/server";
import { connectToDatabase } from "@/lib/mongodb";
import nodemailer from "nodemailer";
import bcrypt from "bcryptjs";

function generateRandomPassword(length = 10) {
  const chars = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789";
  let pass = "";
  for (let i = 0; i < length; i++) {
    pass += chars.charAt(Math.floor(Math.random() * chars.length));
  }
  return pass;
}

export async function POST(request: Request) {
  try {
    const { email } = await request.json();
    if (!email) {
      return NextResponse.json({ error: "Missing email" }, { status: 400 });
    }

    const { db } = await connectToDatabase();
    const user = await db.collection("users").findOne({ email, provider: "credentials" });
    if (!user) {
      // Tr·∫£ v·ªÅ th√†nh c√¥ng ƒë·ªÉ tr√°nh l·ªô th√¥ng tin user t·ªìn t·∫°i hay kh√¥ng
      return NextResponse.json({ message: "N·∫øu email t·ªìn t·∫°i, m·∫≠t kh·∫©u m·ªõi ƒë√£ ƒë∆∞·ª£c g·ª≠i." });
    }

    // T·∫°o m·∫≠t kh·∫©u m·ªõi
    const newPassword = generateRandomPassword(10);
    const hashedPassword = await bcrypt.hash(newPassword, 10);

    // C·∫≠p nh·∫≠t m·∫≠t kh·∫©u m·ªõi v√†o database
    await db.collection("users").updateOne(
      { _id: user._id },
      { $set: { password: hashedPassword } }
    );

    // G·ª≠i email
    const transporter = nodemailer.createTransport({
      service: "gmail",
      auth: {
        user: "YOUR_GMAIL@gmail.com", // Thay b·∫±ng email Gmail c·ªßa b·∫°n
        pass: "YOUR_APP_PASSWORD",    // Thay b·∫±ng app password 16 k√Ω t·ª±
      },
    });

    await transporter.sendMail({
      from: '"AISH Support" <YOUR_GMAIL@gmail.com>',
      to: email,
      subject: "M·∫≠t kh·∫©u m·ªõi cho t√†i kho·∫£n AISH",
      text: `M·∫≠t kh·∫©u m·ªõi c·ªßa b·∫°n l√†: ${newPassword}\nVui l√≤ng ƒëƒÉng nh·∫≠p v√† ƒë·ªïi l·∫°i m·∫≠t kh·∫©u sau khi ƒëƒÉng nh·∫≠p.`,
