  },
  totalPrice: {
    fontWeight: '600',
    color: '#2C3E50',
  },
  shippingAddress: {
    paddingTop: '20px',
    borderTop: '1px solid #e0e0e0',
  },
  addressTitle: {
    fontSize: '14px',
    fontWeight: '600',
    color: '#2C3E50',
    marginBottom: '8px',
  },
  address: {
    fontSize: '14px',
    color: '#666',
    lineHeight: '1.5',
  },

  '@media (max-width: 768px)': {
    orderContent: {
      flexDirection: 'column',
    },
    productsSection: {
      width: '100%',
      paddingRight: 0,
      marginBottom: '20px',
    },
    orderDetailsSection: {
      width: '100%',
      borderLeft: 'none',
      paddingLeft: 0,
      borderTop: '1px solid #e0e0e0',
      paddingTop: '20px',
    },
  },

  continueShopping: {
    display: 'flex',
    justifyContent: 'center',
    marginTop: '100px',
    marginBottom: '100px',
  },

  orderDivider: {
    height: '1px',
    backgroundColor: '#e5e5e5',
    width: '100%',
    margin: '30px 0',
  },
};
